#pragma once

#include "CoreMinimal.h"
#include "GameFramework/GameModeBase.h"
#include "BasketballMatchComponent.h"
#include "BasketBallGameGameMode.generated.h"

class ABasketBallGameState;

/**
 * Basketball GameMode
 * Authority layer for match orchestration.
 * No UI bindings. No Blueprint delegates.
 * Pure C++ orchestration.
 */
UCLASS()
class ABasketBallGameGameMode : public AGameModeBase
{
    GENERATED_BODY()

public:

    ABasketBallGameGameMode();

    virtual void BeginPlay() override;

    // ================= MATCH TIMER =================

    UPROPERTY(EditDefaultsOnly, Category = "Match")
    int32 MatchDurationSeconds = 300;

    void StartMatch();
    void EndMatch();

    // ================= SCORING WRAPPERS =================

    UFUNCTION(BlueprintCallable)
    void RegisterShotAttempt();

    UFUNCTION(BlueprintCallable)
    void RegisterShotSuccess(int32 PointValue);

    UFUNCTION(BlueprintCallable)
    void RegisterShotMiss();

    // ================= ACCESSORS =================

    UFUNCTION(BlueprintPure)
    UBasketballMatchComponent* GetMatchComponent() const { return MatchComponent; }

    ABasketBallGameState* GetBasketBallGameState() const;

private:

    void UpdateMatchTimer();

    FTimerHandle MatchTimerHandle;

    UPROPERTY(VisibleAnywhere)
    TObjectPtr<UBasketballMatchComponent> MatchComponent;
};
