#pragma once

#include "CoreMinimal.h"
#include "GameFramework/GameModeBase.h"
#include "BasketBallGameGameMode.generated.h"

class UBasketballScoreManager;

/**
 *  Basketball Game Mode
 *  Manages scoring, round timer, and game state
 */
UCLASS(abstract)
class ABasketBallGameGameMode : public AGameModeBase
{
	GENERATED_BODY()

public:

	/** Constructor */
	ABasketBallGameGameMode();

	/** Score manager component */
	UPROPERTY(VisibleAnywhere, BlueprintReadOnly, Category = "Components", meta = (AllowPrivateAccess = "true"))
	UBasketballScoreManager* ScoreManager;

	/** Returns ScoreManager subobject */
	FORCEINLINE UBasketballScoreManager* GetScoreManager() const { return ScoreManager; }
};
